func EncodeContractFunc(contractsABI string, name string, params ...interface{}) ([]byte, error) {
	parsedABI, err := abi.JSON(strings.NewReader(contractsABI))
	if err != nil {
		return nil, err
	}
	return parsedABI.Pack(name, params...)
}
